mapper.select:
	select *from(select @rownum:=@rownum+1  rn, A.* from (select * from qna order by qna_re_step asc) A, (select @rownum := 0) R
 where 1=1) list
 where rn >= #{start} and rn <=#{end}

mapper.delete:
update qna 
set BRDDELETEFLAG = 'N'
where qna_subject = #{qna_subject}

mapper.select:
select *from(select @rownum:=@rownum+1  rn, A.* from (select * from qna where qna_writer = #{id} order by qna_re_step asc) A, (select @rownum := 0) R
 where 1=1) list
 where rn >= #{start} and rn <=#{end}
